import React, { useState } from 'react';
import axios from 'axios';
import LeftSidebar from '../../pages/LeftSidebar/LeftSidebar';
const Chatbot = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOTP] = useState('');
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleAuthenticate = async () => {
    try {
      // Send a request to the backend to authenticate the phone number and generate OTP
      const response = await axios.post('/authenticate', { phoneNumber });
      setErrorMessage('');
      // Handle successful authentication
    } catch (error) {
      setErrorMessage('Failed to authenticate. Please try again.');
    }
  };

  const handleChat = async () => {
    try {
      // Send a request to the backend with the authenticated phone number, OTP, and question
      const response = await axios.post('/chat', { phoneNumber, otp, question });
      setAnswer(response.data.answer);
      setErrorMessage('');
      // Handle the chatbot response
    } catch (error) {
      setErrorMessage('Failed to process the chatbot query. Please try again.');
    }
  };

  return (
    <div className="chatbot-container">
      <LeftSidebar/>
      <h1>Chatbot</h1>
      <div className="input-group">
        <label htmlFor="phone-number">Phone Number:</label>
        <input
          type="text"
          id="phone-number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
        />
      </div>
      <button className="authenticate-btn" onClick={handleAuthenticate}>Authenticate</button>
      <div className="input-group">
        <label htmlFor="otp">OTP:</label>
        <input
          type="text"
          id="otp"
          value={otp}
          onChange={(e) => setOTP(e.target.value)}
        />
      </div>
      <div className="input-group">
        <label htmlFor="question">Question:</label>
        <input
          type="text"
          id="question"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
      </div>
      <button className="chat-btn" onClick={handleChat}>Chat</button>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      {answer && <p className="answer">Answer: {answer}</p>}
    </div>
  );
};

export default Chatbot;
