import React, { useState } from 'react';
import LeftSidebar from '../../components/LeftSidebar/LeftSidebar';
import './Subscription.css';
import { loadStripe } from '@stripe/stripe-js';

const Subscription = () => {
  const [planId, setPlanId] = useState('');
  const [customerId, setCustomerId] = useState('');
  const [subscription, setSubscription] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [showCardOptions, setShowCardOptions] = useState(false);

  const stripePromise = loadStripe('pk_test_51NCJyUSEV4yMd4uhdIvt78aoSEGKi0K0X3f8qf26UEwkSoiK7ROYYFoQZjTJCDAiyEb73bOMeaJjTR56X2HAe9zA00lHL63Oga');

  const handleSubscribe = async () => {
    if (planId && customerId) {
      try {
        const stripe = await stripePromise;

        // Create payment method using Stripe library
        const { error, paymentMethod } = await stripe.createPaymentMethod({
          type: 'card',
          card: {
            number: '4242424242424242', // Replace with the actual card number
            exp_month: 12, // Replace with the actual expiration month
            exp_year: 2023, // Replace with the actual expiration year
            cvc: '123', // Replace with the actual CVC code
          },
        });

        if (error) {
          setErrorMessage(error.message);
          return;
        }

        // Send the payment method and other details to your server to create a subscription
        const response = await fetch('/subscriptions', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            paymentMethodId: paymentMethod.id,
            planId,
            customerId,
          }),
        });

        const { subscription } = await response.json();

        // Set the created subscription
        setSubscription(subscription);
        setErrorMessage('');
      } catch (error) {
        setErrorMessage('Failed to create a subscription. Please try again.');
      }
    } else {
      setErrorMessage('Please select a plan and provide a customer ID.');
    }
  };

  const toggleCardOptions = () => {
    setShowCardOptions(!showCardOptions);
  };

  return (
    <div className="subscription-container">
      <LeftSidebar />
      <div className="subscription-content">
        <h1>Subscription</h1>
        <div className="input-group">
          <label htmlFor="plan-id">Plan:</label>
          <select id="plan-id" value={planId} onChange={(e) => setPlanId(e.target.value)}>
            <option value="">Select a plan</option>
            <option value="free">Free Plan</option>
            <option value="silver">Silver Plan</option>
            <option value="gold">Gold Plan</option>
          </select>
        </div>
        <div className="input-group">
          <label htmlFor="customer-id">Customer ID:</label>
          <input
            type="text"
            id="customer-id"
            value={customerId}
            onChange={(e) => setCustomerId(e.target.value)}
          />
        </div>
        <div className="payment-options">
          <button className="payment-option" onClick={toggleCardOptions}>
            Payment Options
          </button>
          {showCardOptions && (
            <div className="card-options">
              <div className="input-group">
                <label htmlFor="card-number">Card Number:</label>
                <input type="text" id="card-number" placeholder="4242 4242 4242 4242" />
              </div>
              <div className="input-group">
                <label htmlFor="expiry-date">Expiry Date:</label>
                <input type="text" id="expiry-date" placeholder="MM/YY" />
              </div>
              <div className="input-group">
                <label htmlFor="cvc">CVC:</label>
                <input type="text" id="cvc" placeholder="123" />
              </div>
            </div>
          )}
        </div>
        <button className="subscribe-btn" onClick={handleSubscribe}>
          Subscribe
        </button>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        {subscription && (
          <div className="subscription-details">
            <p>Subscription created:</p>
            <p>Plan: {subscription.plan.name}</p>
            <p>Price: {subscription.plan.price}</p>
            <p>Questions Remaining: {subscription.questionsRemaining}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Subscription;
